function PopupWithForm({title, name, children, isOpen, onClose}) {
  return (
    <div className={`popup popup_type__${name} ${isOpen ? 'popup_opened':''}`}>
    <div className="popup__container">
        <h2 className="popup__title">{title}</h2>
        {children}
        <button type="button" className={`popup__btn-close`}></button>
        <form className="popup__form" name={`${name}-form`} id="popupEditForm" action="#" method="get" noValidate>
        <button type="submit" className="popup__btn-save popup__btn-save_disabled" onClick={onClose} title="Сохранить">Сохранить</button>
        </form>
    </div>
</div>
  );
}

export default PopupWithForm;
