import React, { useState, useEffect } from "react";
import { api } from "../utils/Api.js";

function Main(props) {
    const [userAvatar, setUserAvatar] = useState('');
    const [userName, setUserName] = useState('');
    const [userAbout, setUserAbout] = useState('');
    const [cards, setCards] = useState([]);

useEffect(() => {
 api.getInitialUser()
 .then((info) => {
    setUserName(info.name);
    setUserAbout(info.info);
    setUserAvatar(info.avatar);
  })
  .catch(err => alert(err));

  api.getInitialCards()
  .then(() => {
      setCards(cards);
  })
  .catch(err => alert(err));
}, []);

return (
<main className="content">
            <section className="profile">
                <div className="profile__update-photo">
                <img className="profile__avatar" alt="аватар" onClick={props.onEditAvatar} src={userAvatar}/>
            </div>
                <div className="profile__info">
                    <h1 className="profile__title">{userName}</h1>
                    <button className="profile__edit-button" onClick={props.onEditProfile} type="button" aria-label="Edit"
                        title="Редактировать данные"></button>
                    <p className="profile__subtitle">{userAbout}</p>
                </div>
                <button className="profile__add-button" onClick={props.onAddPlace} type="button" aria-label="Add" title="Добавить данные"></button>
            </section>

            {/* <section className="elements">
                <ul className="elements__cards">
                </ul>
            </section> */}
        </main>
        );
}

export default Main;
